all_levels = [level1, level2]


def assemble_level(index):
    Barrier.instances = []
    x = 0
    y = 0
    for row in all_levels[index]:
        for char in row:
            if char == "E":
                Barrier("Blank", x, y)
            elif char == " ":
                continue
            x += 1
        y += 1
        x = 0
