def compile_level(level):
    Barrier.instances = []
    x = 0
    y = 0
    for row in level:
        for col in row:
            if col == "E":
                Barrier("Blank", x, y)
            elif col == "F":
                flow = Flow(x, y, .65, .85)
            elif col == "C":
                current = Current(x, y, .65, .85)
            elif col == " ":
                continue
            x += 1
        y += 1
        x = 0
    return flow, current
